SELECT h.hacker_id, h.name, SUM(ms.score) AS total_score FROM Hackers h JOIN (SELECT hacker_id, challenge_id, MAX(score) AS score FROM Submissions GROUP BY hacker_id, challenge_id) ms ON h.hacker_id = ms.hacker_id GROUP BY h.hacker_id, h.name HAVING total_score > 0 ORDER BY total_score DESC, h.hacker_id;
